import { useEffect, useState } from "react";
import React from "react";
import { useDispatch, useSelector, shallowEqual } from "react-redux";
import WifiIcon from "@mui/icons-material/Wifi";
import CameraswitchIcon from "@mui/icons-material/Cameraswitch";
import ElevatorIcon from "@mui/icons-material/Elevator";
import styles from "./todolist.module.css";
import TextField from "@mui/material/TextField";
import { Link } from "react-router-dom";
import { getHotelRooms } from "./api";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";
import Box from "@mui/material/Box";
import Slider from "@mui/material/Slider";
import FormGroup from "@mui/material/FormGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import Footer from "../Components/footer/Footer";
function valuetext(value) {
  return `${value}`;
}

function HotelItem({
  name,
  location,
  city,
  description,
  rating,
  price,
  poster,
  images,
  discount,
  id
}) {
  return (
    <>
      <div style={{ display: "flex", justifyContent: "left" }}>
        <div style={{ display: "flex" }}>
          <div>
            <img src={poster} alt="img" height="76%" width="96%" />
          </div>
          <div style={{ width: "10%" }}>
            <img src={images[1]} alt="img" height="95px" width="120px" />
            <img src={images[0]} alt="img" height="95px" width="120px" />
            <img src={images[2]} alt="img" height="95px" width="120px" />
          </div>
          <div style={{ marginLeft: "40px", textAlign: "left" }}>
            <div>
              <span style={{ fontSize: "20px", fontWeight: "700" }}>
                {name}
              </span>
            </div>
            <p>{location}</p>
            <div style={{ display: "flex" }}>
              <div>
                <button
                  style={{
                    background: "rgb(82,181,32)",
                    color: "white",
                    border: "none",
                    borderRadius: "5px"
                  }}
                >
                  {rating}
                </button>
                ({rating * 10} Ratings) .{rating > 4 ? "Very Good" : "Good"}
              </div>
            </div>
            <div style={{ display: "flex" }}>
              <div>
                <p> AC</p>
              </div>
              <div>
                <p> Parking Facility</p>{" "}
              </div>
              <div>
                {" "}
                <p> Reception</p>{" "}
              </div>
            </div>
            <div style={{ display: "flex" }}>
              <div>
                <p>
                  <WifiIcon /> Free Wifi
                </p>
              </div>
              <div>
                <p> TV</p>
              </div>
              <div>
                <p>
                  <CameraswitchIcon /> CCTV Camera
                </p>
              </div>
              <div>
                <p>
                  <ElevatorIcon /> Elevator
                </p>
              </div>
            </div>
            <div style={{ display: "flex" }}>
              <div>{rating > 4 ? "WIZARD MEMBER" : "Vaccinated Staff"}</div>
            </div>
            <div style={{ display: "flex" }}>
              <div>
                <div>
                  <p style={{ lineHeight: "5px" }}>
                    <span
                      style={{
                        fontSize: "20px",
                        fontWeight: "700",
                        lineHeight: "24px",
                        color: "rgb(238,42,35)"
                      }}
                    >
                      {price}
                    </span>
                    <span
                      style={{
                        textDecoration: "line-through",
                        fontSize: "10px",
                        lineHeight: "25px",
                        color: "rgb(180,186,188)"
                      }}
                    >
                      {((price * 100) / (100 - discount)).toFixed(2)}
                    </span>
                    <span style={{ color: "rgb(244,165,34)" }}>
                      {discount}% off
                    </span>
                  </p>
                </div>
                <div>
                  <p
                    style={{
                      color: "rgb(163,169,172)",
                      fontSize: "12px"
                    }}
                  >
                    per room per night
                  </p>
                </div>
              </div>
              <div style={{ marginLeft: "40px" }}>
                <Stack direction="row" spacing={2}>
                  <Button
                    variant="out"
                    style={{ color: "balck", border: "1px solid black" }}
                  >
                    <Link
                      to={`/oyorooms/${city}/${id}`}
                      style={{ textDecoration: "none", color: "black" }}
                    >
                      Show more info
                    </Link>
                  </Button>
                  <Button
                    variant="contained"
                    style={{ background: "rgb(25,181,79)" }}
                  >
                    Book Now
                  </Button>
                </Stack>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

function DisplayHotel() {
  const [mail, setMail] = React.useState("e.g. abc@gmail.com");
  const [sortBy, setSortBy] = useState("popularity");
  const [filterBy, setFilterBy] = useState("");
  const [bangaloreData, setBangaloreData] = useState([]);
  const handleMailChange = (event) => {
    setMail(event.target.value);
  };
  const [value, setValue] = React.useState([20, 37]);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  let { hotelDataArray, isLoading, isError } = useSelector(
    (state) => state.app,
    shallowEqual
  );

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getHotelRooms());
  }, []);

  const handleSortBy = (e) => {
    setSortBy(e.target.value);
  };

  switch (sortBy) {
    case "rating": {
      hotelDataArray.sort((a, b) => a.rating - b.rating);
      break;
    }
    case "lowtohigh": {
      hotelDataArray.sort((a, b) => a.price - b.price);
      break;
    }
    case "hightolow": {
      hotelDataArray.sort((a, b) => b.price - a.price);
      break;
    }
    case "popularity": {
      hotelDataArray.sort((a, b) => a.popularity - b.popularity);
      break;
    }
    default:
      return hotelDataArray;
  }

  const handleFilterClick = (e) => {
    setFilterBy(e.target.textContent);
    console.log(filterBy);
  };

  return (
    <>
      <div
        style={{
          background: "rgb(222,150,64)",
          display: "flex",
          justifyContent: "center"
        }}
      >
        <div>
          <span style={{ fontSize: "30px" }}></span>
        </div>
        <div style={{ padding: "12px" }}>
          <span style={{ fontSize: "12px", lineHeight: "16px" }}>
            Please check the travel advisory issued by the concerned state
            government/local authorities before booking, as some places may have
            COVID-19 related travel/lodging restrictions.
          </span>
        </div>
      </div>
      <div
        style={{
          display: "flex",
          padding: "20px",
          textAlign: "left"
        }}
      >
        <div
          style={{
            width: "20%",
            borderRight: "1px solid grey",
            paddingRight: "20px"
          }}
        >
          <div>
            <h2>Filters</h2>
          </div>
          <div>
            <h5>Popular locations in Delhi, India</h5>
          </div>
          <div style={{ margin: "10px" }}>
            <input
              type="text"
              placeholder="Search..."
              style={{ padding: "10px" }}
            />
          </div>
          <div style={{ margin: "10px" }}>
            <Button
              variant="outlined"
              onClick={handleFilterClick}
              style={{
                background: "rgb(242,242,242)",
                color: "rgb(34,34,34)",
                border: "none"
              }}
            >
              Bangalore
            </Button>
          </div>
          <div style={{ margin: "10px" }}>
            <Button
              variant="outlined"
              onClick={handleFilterClick}
              style={{
                background: "rgb(242,242,242)",
                color: "rgb(34,34,34)",
                border: "none"
              }}
            >
              Delhi
            </Button>
          </div>
          <div style={{ margin: "10px" }}>
            <Button
              variant="outlined"
              onClick={handleFilterClick}
              style={{
                background: "rgb(242,242,242)",
                color: "rgb(34,34,34)",
                border: "none"
              }}
            >
              Gurgaon
            </Button>
          </div>
          <div style={{ margin: "10px" }}>
            <Button
              variant="outlined"
              onClick={handleFilterClick}
              style={{
                background: "rgb(242,242,242)",
                color: "rgb(34,34,34)",
                border: "none"
              }}
            >
              Nagpur
            </Button>
          </div>
          <div style={{ margin: "10px" }}>
            <Button
              variant="outlined"
              onClick={handleFilterClick}
              style={{
                background: "rgb(242,242,242)",
                color: "rgb(34,34,34)",
                border: "none"
              }}
            >
              Mumbai
            </Button>
          </div>
          <div style={{ margin: "10px" }}>
            <div>
              <Button
                variant="outlined"
                style={{
                  background: "none",
                  color: "red",
                  border: "none",
                  fontSize: "14px",
                  fontWeight: "600"
                }}
              >
                + View More
              </Button>
            </div>
            <hr style={{ border: ".2px solid rgb(224,224,224)" }} />
            <div>
              <h4>Price </h4>
              <Box>
                <Slider
                  getAriaLabel={() => "Temperature range"}
                  value={value}
                  onChange={handleChange}
                  valueLabelDisplay="auto"
                  getAriaValueText={valuetext}
                />
              </Box>
            </div>
          </div>
          <hr style={{ border: ".2px solid rgb(224,224,224)" }} />
          <div>
            <h4>Collections</h4>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                gap: "1rem",
                fontSize: "12px",
                fontWeight: "200"
              }}
            >
              <div>
                <FormGroup>
                  <FormControlLabel
                    control={<Checkbox defaultChecked />}
                    label="sanitised b4 ur eyes"
                  />
                </FormGroup>
              </div>
              <div>
                <FormGroup>
                  <FormControlLabel
                    control={<Checkbox />}
                    label="vaccinated Staff"
                  />
                </FormGroup>
              </div>

              <div>
                <FormGroup>
                  <FormControlLabel
                    control={<Checkbox />}
                    label="Family OYOs"
                  />
                </FormGroup>
              </div>

              <div>
                <FormGroup>
                  <FormControlLabel
                    control={<Checkbox />}
                    label="For Group Travellers"
                  />
                </FormGroup>
              </div>

              <div>
                <FormGroup>
                  <FormControlLabel
                    control={<Checkbox />}
                    label="Near Airport"
                  />
                </FormGroup>
              </div>
              <div>
                <Button
                  variant="outlined"
                  style={{
                    background: "none",
                    color: "red",
                    border: "none",
                    fontSize: "14px",
                    fontWeight: "600"
                  }}
                >
                  + View More
                </Button>
              </div>
            </div>
            <hr style={{ border: ".2px solid rgb(224,224,224)" }} />
            <h4>Categories</h4>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                gap: "1rem",
                fontWeight: "300",
                fontSize: "12"
              }}
            >
              <div style={{ display: "flex" }}>
                <div style={{ marginRight: "5px" }}>
                  <input
                    type="checkbox"
                    name="OYO Rooms"
                    label="Oyo"
                    style={{
                      height: "18px",
                      width: "18px",
                      cursor: "pointer"
                    }}
                  />{" "}
                </div>
                <div>
                  <span style={{ fontWeight: "600" }}> OYO Rooms</span>
                  <span style={{ lineHeight: "25px", fontSize: "14px" }}>
                    - Super affordable stays with essential aminities
                  </span>
                </div>
              </div>
              <div style={{ display: "flex" }}>
                <div style={{ marginRight: "5px" }}>
                  <input
                    type="checkbox"
                    name="OYO Rooms"
                    label="Oyo"
                    style={{
                      height: "18px",
                      width: "18px",
                      cursor: "pointer"
                    }}
                  />{" "}
                </div>
                <div>
                  <span style={{ fontWeight: "600" }}> Premium</span>
                  <span style={{ lineHeight: "25px", fontSize: "14px" }}>
                    - Hotel at prime location and premium aminities
                  </span>
                </div>
              </div>
              <div style={{ display: "flex" }}>
                <div style={{ marginRight: "5px" }}>
                  <input
                    type="checkbox"
                    name="OYO Rooms"
                    label="Oyo"
                    style={{
                      height: "18px",
                      width: "18px",
                      cursor: "pointer"
                    }}
                  />{" "}
                </div>
                <div>
                  <span style={{ fontWeight: "600" }}> Townhouse </span>
                  <span style={{ lineHeight: "25px", fontSize: "14px" }}>
                    - Your friendly, premium neighbourhood hotel-Serviced by OYO
                  </span>
                </div>
              </div>
              <div style={{ display: "flex" }}>
                <div style={{ marginRight: "5px" }}>
                  <input
                    type="checkbox"
                    name="OYO Rooms"
                    label="Oyo"
                    style={{
                      height: "18px",
                      width: "18px",
                      cursor: "pointer"
                    }}
                  />{" "}
                </div>
                <div>
                  <span style={{ fontWeight: "600" }}> Flagship </span>
                  <span style={{ lineHeight: "25px", fontSize: "14px" }}>
                    - Affordable hotels at prime locations - Serviced by OYO
                  </span>
                </div>
              </div>

              <div style={{ display: "flex" }}>
                <div style={{ marginRight: "5px" }}>
                  <input
                    type="checkbox"
                    name="OYO Rooms"
                    label="Oyo"
                    style={{
                      height: "18px",
                      width: "18px",
                      cursor: "pointer"
                    }}
                  />{" "}
                </div>
                <div>
                  <span
                    style={{
                      fontWeight: "600"
                    }}
                  >
                    {" "}
                    OYOX Design{" "}
                  </span>{" "}
                  <span style={{ lineHeight: "25px", fontSize: "14px" }}>
                    - Beautiful designed private crafted for the traveller who
                    craves comfort
                  </span>
