// import axios from "axios";
import { useEffect, useState } from "react";
import { shallowEqual, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import "react-alice-carousel/lib/alice-carousel.css";
import { Slide } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";
import WifiIcon from "@mui/icons-material/Wifi";
import CameraswitchIcon from "@mui/icons-material/Cameraswitch";
import ElevatorIcon from "@mui/icons-material/Elevator";

const getDetails = () => {
  return fetch("https://oyo-server.herokuapp.com/hotel")
    .then((res) => res.json())
    .then((res) => {
      return res;
    })
    .catch((res) => {});
};
// var data = [];
var slideImages = [];
// HOOkS
function UserDetails() {
  const [data, setData] = useState([]);
  const [roomDetails, setRoomDetails] = useState([]);
  const itemDetails = useParams();
  useEffect(() => {
    getDetails().then((res) => {
      setData(res);
      setRoomDetails(
        data?.filter((rooms) => rooms.id === parseInt(itemDetails.id))
      );
    });
    slideImages = [
      {
        url: roomDetails[0]?.poster,
      },
      {
        url: roomDetails[0]?.images[1],
      },
      {
        url: roomDetails[0]?.images[2],
      },
      {
        url: roomDetails[0]?.images[3],
      },
    ];
  }, [roomDetails]);
  console.log(roomDetails);

  let { isLoading, isError } = useSelector((state) => state.app, shallowEqual);
  if (isLoading) return <div>...loading</div>;
  return (
    <>
      <div>
        <div>
          <Slide>
            {slideImages.map((slideImage, index) => (
              <div className="each-slide" key={index}>
                <div
                  style={{
                    backgroundImage: `url(${slideImage.url})`,
                    height: "600px",
                    width: "100%",
                  }}
                >
                  {/* <span>{slideImage.caption}</span> */}
                </div>
              </div>
            ))}
          </Slide>
        </div>
      </div>
      <div>
        {roomDetails.map((i) => (
          <>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                textAlign: "left",
              }}
            >
              <div style={{ display: "flex" }}>
                <div>
                  <h2>{i.name}</h2>
                  <p>{i.location}</p>
                </div>
                <div>
                  <h3>{i.rating}</h3>
                </div>
              </div>
              <div style={{ textAlign: "left", width: "50%" }}>
                <h2>Description</h2>
                <p>{i.descripition}</p>
                <div style={{ color: "red" }}>
                  <h3>Read more</h3>
                </div>
              </div>
              <div>
                <h2>Amenities</h2>
              </div>
              <div style={{ display: "flex", gap: "2rem" }}>
                <div>
                  <p>üõè AC</p>
                </div>
                <div>
                  <p>üöò Parking Facility</p>{" "}
                </div>
                <div>
                  {" "}
                  <p>üôè Reception</p>{" "}
                </div>
              </div>
              <div style={{ display: "flex", gap: "2rem" }}>
                <div>
                  <p>
                    <WifiIcon /> Free Wifi
                  </p>
                </div>
                <div>
                  <p>üì∫ TV</p>
                </div>
                <div>
                  <p>
                    <CameraswitchIcon /> CCTV Camera
                  </p>
                </div>
                <div>
                  <p>
                    <ElevatorIcon /> Elevator
                  </p>
                </div>
              </div>
              <div style={{ color: "red" }}>
                <h3>Show More</h3>
              </div>
              <div>
                <div>
                  <h2>Choose your room</h2>
                </div>
                <div>
                  <div>
                    <p>‚òÖ SELECTED CATEGORY</p>
                  </div>
                  <div style={{ display: "flex" }}>
                    <div>
                      <div>
                        <h2>Classic (2X)</h2>
                        <p>Room size: 156 sqft</p>
                      </div>
                      <div style={{ display: "flex", gap: "1rem" }}>
                        <div>
                          <p>
                            <WifiIcon /> Free Wifi
                          </p>
                        </div>
                        <div>
                          <p>üì∫ TV</p>
                        </div>
                        <div>
                          <p>
                            <CameraswitchIcon /> CCTV Camera
                          </p>
                        </div>
                        <div>
                          <p>
                            <ElevatorIcon /> Elevator
                          </p>
                        </div>
                      </div>
                    </div>
                    <div>
                      <img src={i.images[0]} alt="img" height="160px" />
                    </div>
                  </div>
                  {/* <div>
                      <h3>
                        ‚Çπ{i.price}
                      </h3>
                      <span>‚Çπ{}</span>
                    </div> */}
                  <div>
                    <p style={{ lineHeight: "5px" }}>
                      <span
                        style={{
                          fontSize: "20px",
                          fontWeight: "700",
                          lineHeight: "24px",
                          color: "rgb(238,42,35)",
                        }}
                      >
                        ‚Çπ{i.price}
                      </span>
                      <span
                        style={{
                          textDecoration: "line-through",
                          fontSize: "16px",
                          lineHeight: "25px",
                          color: "rgb(180,186,188)",
                        }}
                      >
                        ‚Çπ{((i.price * 100) / (100 - i.discount)).toFixed(2)}
                      </span>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </>
        ))}
      </div>
    </>
  );
}

export default UserDetails;
